import { useParams } from "react-router-dom";
// import useFetch from "../useFetch";

import { useState, useEffect } from "react";

const useFetch = (url, blogId = null) => {
  const [data, setData] = useState(null);
  const [isPending, setIsPending] = useState(false);
  const [error, setError] = useState(null);

  const Blog = ({ match }) => {
    const { data, isPending, error } = useFetch(
      "https://traveling-blog-24f80-default-rtdb.europe-west1.firebasedatabase.app/blogs",
      match.params.id
    );

    return (
      <div className="blog">
        {error && <div> {error} </div>}
        {isPending && <div className="loading-message">Loading...</div>}
        {data && <div className="blog-content">{data}</div>}
      </div>
    );
  };

  useEffect(() => {
    setIsPending(true);
    fetch(`${url}${blogId ? `/${blogId}.json` : ".json"}`)
      .then((res) => res.json())
      .then((result) => {
        setData(result);
        setIsPending(false);
      })
      .catch((err) => {
        setError(err);
        setIsPending(false);
      });
  }, [blogId, url]);

  return { data, isPending, error };
};

export default useFetch;
