import { useHistory, useParams } from "react-router-dom";
import useFetch from "../useFetch";

const BlogDetails = () => {
  const { id } = useParams();
  const {
    data: blog,
    error,
    isPending,
  } = useFetch(
    `https://my-json-server.typicode.com/mihai-gabriel1/demo-apidb/blogs/${id}`
  );
  const history = useHistory();

  const handleDelete = () => {
    fetch(
      "https://my-json-server.typicode.com/mihai-gabriel1/demo-apidb/blogs/" +
        blog.id,
      {
        method: "DELETE",
      }
    ).then(() => {});
  };

  return (
    <div className="blog-details">
      {isPending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {blog && (
        <article>
          <h2> {blog.title}</h2>
          <p>Written by {blog.author}</p>
          <div>{blog.body}</div>
          <button onClick={handle}>delete blog</button>
        </article>
      )}
    </div>
  );
};

export default BlogDetails;
